try:
    result = 10 / 2
    print(f"Division result: {result}")
except ZeroDivisionError as e:
    print(f"Error: {e}")
except Exception as e:
    print(f"Unexpected error: {e}")
finally:
    print("Execution completed")

count = 0
while True:
    count += 1
    if count % 100000 == 0:
        print(f"Count: {count}")
