def process_data(data):
    result = []
    for item in data:
        if item > 0:
            result.append(item * 2)
    return result

comparison_examples.code_v2:
def sort_list(items):
    """Sort a list in ascending order."""
    if not items:
        return []
    return sorted(items, key=lambda x: x, reverse=False)
